mapper.select:
SELECT
ID,BORROW_ID,BORROW_USER_ID,BORROW_TITLE,DEADLINE,
REPAYMENT_TIME,TOTAL_AMOUNT,PRINCIPAL,INTEREST,PERIOD,
STATE,BORROW_TYPE,REPAYMENT_TYPE,CREATE_TIME
FROM T_REPAYMENT

    
        and deadline > #{beginDate} and deadline < #{endDate}
    
    
        and state = #{state}
    

order by repayment_time

    LIMIT #{beginPos},#{pageSize}

SELECT COUNT(1) FROM T_REPAYMENT

    
        and id = #{id}
    
    
        and borrow_id = #{borrowId}
    
    
        and borrow_user_id = #{borrowUserId}
    
    
        and borrow_title = #{borrowTitle}
    
    
        and deadline = #{deadline}
    
    
        and repayment_time = #{repaymentTime}
    
    
        and total_amount = #{totalAmount}
    
    
        and principal = #{principal}
    
    
        and interest = #{interest}
    
    
        and period = #{period}
    
    
        and state = #{state}
    
    
        and borrow_type = #{borrowType}
    
    
        and repayment_type = #{repaymentType}
    
    
        and create_time = #{createTime}

mapper.update:
UPDATE t_repayment
SET repayment_time = #{repaymentTime},
    state          = #{state}
WHERE id = #{id}

mapper.select:
SELECT id, bid_id, borrow_id, repayment_id, borrow_user_id, bid_user_id, borrow_title, total_amount, principal, interest, period, deadline, repayment_time, repayment_type, create_time FROM t_repayment_detail
and repayment_time > #{beginDate} and repayment_time < #{endDate}
and repayment_type = #{repaymentType}
ORDER BY repayment_time LIMIT #{beginPos},#{pageSize}

SELECT COUNT(1) FROM t_repayment_detail

    
        and repayment_time > #{beginDate} and repayment_time < #{endDate}
    
    
        and repayment_type = #{repaymentType}

mapper.update:
UPDATE T_REPAYMENT
SET STATE = #{state}
WHERE ID = #{id}
